function [W,c_local]=generateW(m,N,A,discount,s)	fact=N*A/m;	W=zeros(m+1,N*A);	for i=1:m	 W(i,(i-1)*fact+1:i*fact)=1;	end;c_local=zeros(N,1);	W=zeros(m+1,N*A);	fact=N/m;		for i=1:N		c_local(i)=(1-discount)*discount^(abs(s-i));		end;    c_local=c_local/sum(c_local);	for i=1:m	%W(i,(i-1)*fact+1:i*fact)=1;	ind=(i-1)*fact+1;	ind=lot(c_local,1);	W(i,ind)=1;	end;	W(m+1,s)=1;	%c1(N)=1;	for i=2:A	W(:,(i-1)*N+1:i*N)=W(:,1:N);	end;  